process.on("message",msg=>{let result=msg.data;result.OS.cpumodel=msg.data.OS.cpumodel.split(" ",4).toString().replaceAll(","," ");result.CPU.clk*=1e3;result.OS.cpuclk*=1e3;result.CPU.load=Number.parseInt(result.CPU.load);let n=msg.data.GPU.name.split(" ");result.GPU.vendor=n[1];result.GPU.model=n[2];result.GPU.class=n[3];result.RAM.total=result.firstrun?roundDown(msg.data.RAM.total):result.RAM.total;result.RAM.used=roundDown(msg.data.RAM.used);result.HDD.forEach(element=>{element.size=roundDown(element.size);element.used=roundDown(element.used);element.available=roundDown(element.available)});let x=Date.now()-result.date;result.date=x;result.firstrun=false;process.send(result)});function roundDown(x){return Math.floor(Math.floor(x/1e9)/4)*4}